-- Create the recipe_details table
CREATE TABLE recipe_details (
                                recipe_id UUID PRIMARY KEY,
                                meal_type VARCHAR(50),
                                description TEXT,
                                food_class VARCHAR(50),
                                region VARCHAR(50),
                                spiciness_level VARCHAR(50),
                                main_ingredients VARCHAR(255),
                                cooking_method VARCHAR(50),
                                FOREIGN KEY (recipe_id) REFERENCES recipes(id)
);

-- Create a temporary table to hold the raw data
CREATE TEMPORARY TABLE temp_recipe_details (
    dish_name VARCHAR(255),
    meal_type VARCHAR(50),
    description TEXT,
    food_class VARCHAR(50),
    region VARCHAR(50),
    spiciness_level VARCHAR(50),
    main_ingredients VARCHAR(255),
    cooking_method VARCHAR(50)
);

