-- Insert data from the temporary table into the recipe_details table
SELECT r.id, t.meal_type, t.description, t.food_class, t.region, t.spiciness_level, t.main_ingredients, t.cooking_method
FROM temp_recipe_details t
         JOIN recipes r ON t.dish_name = r.name;

-- Drop the temporary table
DROP TABLE temp_recipe_details;
